package reges.chapter02;

public class C02E06_TriangleOfNumbers {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1;i<=7;i++){
		
			for (int j = 0; j<i; j++){
				System.out.print(i);
			}
			System.out.println();
		}
		
	}

}
