package reges.chapter02;

public class C02E07_SlashOfNumbers {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1;i<=5;i++){
		
			for (int j = 5; j>i; j--){
				System.out.print(" ");
			}
		System.out.print(i);
		System.out.println();
		}
	}
}
